content.script:
function toggle(sender) {
  var e = sender.parentNode.parentNode.nextElementSibling;
  if (e.style.display) {
    e.style.display= null;
    sender.firstChild.src = sender.firstChild.src.replace("plus", "minus");
  } else {
    e.style.display= "none";
    sender.firstChild.src = sender.firstChild.src.replace("minus", "plus");
  }
  return false;
}

